import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { itemsAPI } from '../services/api';
import { toast } from 'react-toastify';
import { FiPlus, FiPackage, FiGift, FiTrendingUp, FiMapPin } from 'react-icons/fi';
import './Dashboard.css';

const Dashboard = () => {
  const { user, isDonor } = useAuth();
  const [myDonations, setMyDonations] = useState([]);
  const [myReceivedItems, setMyReceivedItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserItems();
  }, []);

  const fetchUserItems = async () => {
    try {
      setLoading(true);
      if (isDonor) {
        const response = await itemsAPI.getMyDonations();
        setMyDonations(response.data);
      } else {
        const response = await itemsAPI.getMyReceivedItems();
        setMyReceivedItems(response.data);
      }
    } catch (error) {
      toast.error('Failed to load your items');
    } finally {
      setLoading(false);
    }
  };

  const handleApproveRequest = async (itemId, userId) => {
    try {
      await itemsAPI.donateItem(itemId, userId);
      toast.success('Request approved!');
      fetchUserItems();
    } catch (error) {
      toast.error('Failed to approve request');
    }
  };

  const handleDeleteItem = async (itemId) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      try {
        await itemsAPI.deleteItem(itemId);
        toast.success('Item deleted');
        fetchUserItems();
      } catch (error) {
        toast.error('Failed to delete item');
      }
    }
  };

  const getStatusBadge = (status) => {
    const badges = {
      pending: <span className="badge badge-warning">Pending Approval</span>,
      available: <span className="badge badge-success">Available</span>,
      requested: <span className="badge badge-info">Requested</span>,
      donated: <span className="badge badge-secondary">Donated</span>
    };
    return badges[status];
  };

  return (
    <div className="dashboard-page">
      <div className="container">
        <div className="dashboard-header">
          <div>
            <h1>Welcome back, {user?.name}!</h1>
            <p className="dashboard-subtitle">
              {isDonor 
                ? 'Manage your donations and help those in need' 
                : 'Track items you\'ve received from our community'}
            </p>
          </div>
          {isDonor && (
            <Link to="/donate" className="btn btn-primary">
              <FiPlus /> Donate New Item
            </Link>
          )}
        </div>

        {/* Stats Cards */}
        <div className="stats-grid">
          <div className="stat-card card">
            <div className="stat-icon">
              <FiPackage />
            </div>
            <div className="stat-content">
              <h3>{isDonor ? myDonations.length : myReceivedItems.length}</h3>
              <p>{isDonor ? 'Items Donated' : 'Items Received'}</p>
            </div>
          </div>

          <div className="stat-card card">
            <div className="stat-icon">
              <FiGift />
            </div>
            <div className="stat-content">
              <h3>
                {isDonor 
                  ? myDonations.filter(i => i.status === 'donated').length 
                  : myReceivedItems.length}
              </h3>
              <p>{isDonor ? 'Successfully Donated' : 'Total Received'}</p>
            </div>
          </div>

          <div className="stat-card card">
            <div className="stat-icon">
              <FiTrendingUp />
            </div>
            <div className="stat-content">
              <h3>
                {isDonor 
                  ? myDonations.filter(i => i.status === 'available').length 
                  : '-'}
              </h3>
              <p>{isDonor ? 'Currently Available' : 'Impact Score'}</p>
            </div>
          </div>
        </div>

        {/* Items List */}
        <div className="items-section">
          <h2>{isDonor ? 'My Donations' : 'Items I\'ve Received'}</h2>

          {loading ? (
            <div className="spinner"></div>
          ) : isDonor ? (
            myDonations.length === 0 ? (
              <div className="empty-state card">
                <FiPackage />
                <h3>No donations yet</h3>
                <p>Start sharing items with your community</p>
                <Link to="/donate" className="btn btn-primary">
                  Donate Your First Item
                </Link>
              </div>
            ) : (
              <div className="items-list">
                {myDonations.map(item => (
                  <div key={item._id} className="item-row card">
                    <img src={item.image || '/placeholder.jpg'} alt={item.itemName} />
                    <div className="item-info">
                      <h3>{item.itemName}</h3>
                      <p className="item-desc">{item.description}</p>
                      <div className="item-details">
                        <span className="category-tag">{item.category}</span>
                        <span className="location-tag">
                          <FiMapPin /> {item.location.city}
                        </span>
                      </div>
                    </div>
                    <div className="item-status">
                      {getStatusBadge(item.status)}
                      {item.requests && item.requests.length > 0 && item.status !== 'donated' && (
                        <div className="requests-info">
                          <p>{item.requests.length} request(s)</p>
                          {item.requests.map(req => (
                            <div key={req._id} className="request-item">
                              <p>{req.user.name}</p>
                              <button 
                                onClick={() => handleApproveRequest(item._id, req.user._id)}
                                className="btn btn-sm btn-primary"
                              >
                                Approve
                              </button>
                            </div>
                          ))}
                        </div>
                      )}
                      {item.status === 'pending' && (
                        <button 
                          onClick={() => handleDeleteItem(item._id)}
                          className="btn btn-sm btn-outline"
                        >
                          Delete
                        </button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )
          ) : (
            myReceivedItems.length === 0 ? (
              <div className="empty-state card">
                <FiGift />
                <h3>No items received yet</h3>
                <p>Browse available items to get started</p>
                <Link to="/browse" className="btn btn-primary">
                  Browse Items
                </Link>
              </div>
            ) : (
              <div className="items-list">
                {myReceivedItems.map(item => (
                  <div key={item._id} className="item-row card">
                    <img src={item.image || '/placeholder.jpg'} alt={item.itemName} />
                    <div className="item-info">
                      <h3>{item.itemName}</h3>
                      <p className="item-desc">{item.description}</p>
                      <p className="donor-info">
                        Donated by: {item.donor.name}
                        {item.donor.phone && ` • ${item.donor.phone}`}
                      </p>
                    </div>
                    <div className="item-status">
                      {getStatusBadge(item.status)}
                    </div>
                  </div>
                ))}
              </div>
            )
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
